""" Controller's module for events app """
# Django

from django.contrib.auth.decorators import login_required
from django.shortcuts import render, redirect

# Test


@login_required
def list_events(request):
    """ Get the event's list generated by loged user """
    return render(request, 'events/myevents.html')


@login_required
def create_event(request):
    """ Allow to create new events """
    return render(request, 'events/create.html')


@login_required
def edit_event(request):
    """ Edit information for event """
    if request.method == 'GET':
        print(request.GET)
    return render(request, 'events/edit.html')


@login_required
def delete_event(request):
    """ Delete an event from database """
    if request.method == 'GET':
        print(request.GET)
    return render(request, 'events/delete.html')